        },
        {
          id: uuid(),
          name: "Theodore Duncan",
          timestamp:
            "Mon Nov 25 2019 08:20:24 GMT-0800 (Pacific Standard Time)",
          comment:
            "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!"
        }
      ]
    }
  };

  addComment = comment => {
    let newComments = this.state.currentVideo.comments;
    newComments.unshift(comment);
    this.setState({
      comments: newComments
    });
  };

  Images = props => {
  const images = props.images.map(image => (
    <Link to={`/images/${image.id}`} key={image.id}>
      <img src={image.url} alt={image.title} />
    </Link>
  ));
  return images;
};

  render() {
    return (
      <Router>
        <Switch>
          <Redirect from="/home" to="/" />
          <Route
            path="/"
            exact
            render={() => (
              <>
                <Header />
                <VideoPlayer currentVideo={this.state.currentVideo} />
                <section className="wrapper-1">
                  <section className="wrapper-2">
                    <VideoInfo currentVideo={this.state.currentVideo} />
                    <Comments
                      currentVideo={this.state.currentVideo}
                      addComment={this.addComment}
                    />
                  </section>
                  <VideosList videos={this.state.videos} />
                </section>
              </>
            )}
          />
          <Route
            path="/upload"
            render={() => (
              <>
                <Header />
                <Upload />
              </>
            )}
          />
          )}
        </Switch>
      </Router>
    );
  }
}
