import React, { Component } from "react";
import "./styles/main.css";
import uuid from "uuid/v1";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
  Link
} from "react-router-dom";
import axios from "axios";
import Header from "./components/Header";
import VideoPlayer from "./components/VideoPlayer";
import VideoInfo from "./components/VideoInfo";
import Comments from "./components/Comments";
import VideosList from "./components/VideosList";
import Upload from "./components/Upload";
import Container from "./components/"
const apiKey = "c354bbd6-1b57-4f1e-b0f3-8743d4495710";

export default class App extends Component {
  state = {
    videos: [],
    currentVideo: {},
    loading: false
  };

  addComment = comment => {
    let newComments = this.state.currentVideo.comments;
    newComments.unshift(comment);
    this.setState({
      comments: newComments
    });
  };

  componentDidMount() {
    axios
      .all([
        axios.get(
          `https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=<${apiKey}>`
        ),
        axios.get(
          `https://project-2-api.herokuapp.com/videos?api_key=<${apiKey}>`
        )
      ])
      .then(responseArr => {
        this.setState({
          currentVideo: responseArr[0].data,
          videos: responseArr[1].data.filter(video => {
            return video.id !== "1af0jruup5gu";
          }),
          loading: true
        });
      });
  }



  render() {
    return (
      <Router>
        <Switch>
          <Redirect from="/" exact to="/videos/1af0jruup5gu" />
          <Route
            path="/videos/:id"
            component={Container}
          />
          <Route
            path="/upload"
            render={() => (
              <>
                <Header />
                <Upload />
              </>
            )}
          />
          )}
        </Switch>
      </Router>
    );
  }
}
