import React, { Component } from "react";
import "./styles/main.css";
import uuid from "uuid/v1";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
  Link
} from "react-router-dom";
import axios from "axios";
import Header from "./components/Header";
import VideoPlayer from "./components/VideoPlayer";
import VideoInfo from "./components/VideoInfo";
import Comments from "./components/Comments";
import VideosList from "./components/VideosList";
import Upload from "./components/Upload";

const apiKey = "c354bbd6-1b57-4f1e-b0f3-8743d4495710";

export default class App extends Component {
  state = {
    videos: [],
    currentVideo: {}
  };

  addComment = comment => {
    let newComments = this.state.currentVideo.comments;
    newComments.unshift(comment);
    this.setState({
      comments: newComments
    });
  };

  componentDidMount() {
    // axios
    //   .get(`https://project-2-api.herokuapp.com/videos?api_key=<${apiKey}>`)
    //   .then(response => this.setState({ videos: response.data }));
    axios.all([
      axios.get(`https://project-2-api.herokuapp.com/videos?api_key=<${apiKey}>`),
      axios.get(`https://project-2-api.herokuapp.com/videos?api_key=<${apiKey}>/`)
    ])
      .then(responseArr => {
        //this will be executed only when all requests are complete
        console.log('Date created: ', responseArr[0].data.created_at);
        console.log('Date created: ', responseArr[1].data.created_at);
      });
  }

  render() {
    return (
      <Router>
        <Switch>
          <Redirect from="/home" to="/" />
          <Route
            path="/"
            exact
            render={() => (
              <>
                <Header />
                <VideoPlayer currentVideo={this.state.currentVideo} />
                <section className="wrapper-1">
                  <section className="wrapper-2">
                    <VideoInfo currentVideo={this.state.currentVideo} />
                    <Comments
                      currentVideo={this.state.currentVideo}
                      addComment={this.addComment}
                    />
                  </section>
                  <VideosList videos={this.state.videos} />
                </section>

                <Route
                  path="/video/:id"
                  render={routerProps => {
                    return (
                      <>
                        <Header />
                        <VideoPlayer
                          currentVideo={this.state.currentVideo}
                          {...routerProps}
                        />
                        <section className="wrapper-1">
                          <section className="wrapper-2">
                            <VideoInfo
                              currentVideo={this.state.currentVideo}
                              {...routerProps}
                            />
                            <Comments
                              currentVideo={this.state.currentVideo}
                              {...routerProps}
                              addComment={this.addComment}
                            />
                          </section>
                          <VideosList videos={this.state.videos} />
                        </section>
                      </>
                    );
                  }}
                />
              </>
            )}
          />
          <Route
            path="/upload"
            render={() => (
              <>
                <Header />
                <Upload />
              </>
            )}
          />
          )}
        </Switch>
      </Router>
    );
  }
}
